mapper.select:
select is_hide
from board
where id = #{postId}

select user_id
from board
where id = #{postId}

mapper.update:
update board
set title = #{title}, body=#{body}, is_hide=#{isHide}, modified_at=NOW()
where id = #{postId}

mapper.delete:
update board
set state = 1
where id = #{postId}

mapper.select:
select create_at, modified_at
from board
where id = #{postId}
