mapper.select:
select id
from board_type
where name = #{boardTypeName};

select id
from category
where name = #{categoryName};

mapper.update:
update board
set title = #{title}, body=#{body}, is_hide=#{isHide}, modified_at=NOW()
where id = #{boardId}

update board
set state = 1
where id = #{boardId}

mapper.select:
select is_hide
from board
where id = #{boardId}

select user_id
from board
where id = #{boardId}

select modified_at
from board
where id = #{boardId}

select category
from board
where category = #{category}

select name
from board_type
where name = #{boardType}
