mapper.update:
update board
set title = #{title}, body=#{body}, is_hide=#{isHide}, modified_at=NOW()
where id = #{boardId}

update board
set state = 1
where id = #{boardId}

mapper.select:
select is_hide
from board
where id = #{boardId}

select user_id
from board
where id = #{boardId}

select created_at, modified_at
from board
where id = #{boardId}
