mapper.update:
UPDATE comment
SET body = #{body}
WHERE board_id = #{borad_id}
  AND id = #{id}

mapper.select:
SELECT *
FROM comment_like
         JOIN comment ON comment.id = comment_like.commentId
WHERE comment_like.commentId = #{comment_id}
  AND comment_like.userId = #{user_id}

SELECT *
FROM comment_report
         JOIN comment ON comment.id = comment_report.comment_id
WHERE comment_report.comment_id = #{comment_id}
  AND comment_report.user_id = #{user_id}
